def mix(s1, s2):
    # Count frequencies of lowercase letters in both strings
    freq1 = {}
    freq2 = {}

    for char in s1:
        if 'a' <= char <= 'z':
            freq1[char] = freq1.get(char, 0) + 1

    for char in s2:
        if 'a' <= char <= 'z':
            freq2[char] = freq2.get(char, 0) + 1

    # Collect results
    results = []

    # Get all unique lowercase letters from both strings
    all_chars = set(freq1.keys()) | set(freq2.keys())

    for char in all_chars:
        count1 = freq1.get(char, 0)
        count2 = freq2.get(char, 0)

        # Only consider if maximum frequency > 1
        max_count = max(count1, count2)
        if max_count <= 1:
            continue

        # Determine prefix
        if count1 > count2:
            prefix = "1:"
        elif count2 > count1:
            prefix = "2:"
        else:  # count1 == count2
            prefix = "=:"

        # Create the string
        result_string = prefix + char * max_count
        results.append(result_string)

    # Sort by length (descending) then lexicographically (ascending)
    results.sort(key=lambda x: (-len(x), x))

    return "/".join(results)

