def same_structure_as(original, other):
    # Check if both are lists/arrays
    if isinstance(original, list) and isinstance(other, list):
        # Check if they have the same length
        if len(original) != len(other):
            return False

        # Check each corresponding element
        for i in range(len(original)):
            if isinstance(original[i], list) and isinstance(other[i], list):
                # Both are lists, check recursively
                if not same_structure_as(original[i], other[i]):
                    return False
            elif not isinstance(original[i], list) and not isinstance(other[i], list):
                # Both are not lists, structure matches at this level
                continue
            else:
                # One is list, other is not - structures don't match
                return False

        return True
    else:
        # If either is not a list, they should both not be lists
        return not isinstance(original, list) and not isinstance(other, list)

