    alice_fish = list(map(int, input().split()))
    bob_fish = list(map(int, input().split()))

    # Count frequencies
    alice_count = {}
    bob_count = {}

    for fish in alice_fish:
        alice_count[fish] = alice_count.get(fish, 0) + 1

    for fish in bob_fish:
        bob_count[fish] = bob_count.get(fish, 0) + 1

    # Get all fish types that appear
    all_types = set(alice_count.keys()) | set(bob_count.keys())

    # Check if Bob dominates Alice
    bob_dominates = True
    strict_better = False

    for fish_type in all_types:
        alice_freq = alice_count.get(fish_type, 0)
        bob_freq = bob_count.get(fish_type, 0)

        if bob_freq < alice_freq:
            bob_dominates = False
            break
        elif bob_freq > alice_freq:
            strict_better = True

    if bob_dominates and strict_better:
        print("NO")
    else:
        print("YES")

solve()

