def gcd_of_lcms():
    n = int(input())
    a = list(map(int, input().split()))

    # Function to get prime factorization
    def prime_factorize(x):
        factors = {}
        d = 2
        while d * d <= x:
            while x % d == 0:
                factors[d] = factors.get(d, 0) + 1
                x //= d
            d += 1
        if x > 1:
            factors[x] = factors.get(x, 0) + 1
        return factors

    # Collect all prime factors and their powers for each number
    prime_powers = {}
    for num in a:
        factors = prime_factorize(num)
        for prime, power in factors.items():
            if prime not in prime_powers:
                prime_powers[prime] = []
            prime_powers[prime].append(power)

    # For each prime, calculate its contribution to the final answer
    result = 1
    for prime, powers in prime_powers.items():
        powers.sort()
        # For this prime, we want min over all pairs of max(powers[i], powers[j])
        # This is equivalent to the second smallest power if smallest occurs once,
        # or smallest power if it occurs more than once
        if len(powers) >= 2:
            if powers[0] == powers[1]:
                # Smallest occurs at least twice
                exponent = powers[0]
            else:
                # Smallest occurs once
                exponent = powers[1]
        else:
            # Only one element contributes this prime, so it doesn't appear in any LCM
            exponent = 0

        result *= prime ** exponent

    print(result)

