def solve():
    MOD = 1000000007

    # Read input
    n, m = map(int, input().split())
    grid = []
    for _ in range(n):
        grid.append(input().strip())

    # Precompute how many consecutive rocks are to the right/down from each cell
    # right_push[i][j] = number of rocks that would be pushed right if we move right from (i,j)
    # down_push[i][j] = number of rocks that would be pushed down if we move down from (i,j)

    right_push = [[0] * m for _ in range(n)]
    down_push = [[0] * m for _ in range(n)]

    # Fill right_push array from right to left
    for i in range(n):
        for j in range(m - 2, -1, -1):  # from m-2 to 0
            if grid[i][j + 1] == 'R':
                right_push[i][j] = right_push[i][j + 1] + 1
            else:
                right_push[i][j] = 0

    # Fill down_push array from bottom to top
    for j in range(m):
        for i in range(n - 2, -1, -1):  # from n-2 to 0
            if grid[i + 1][j] == 'R':
                down_push[i][j] = down_push[i + 1][j] + 1
            else:
                down_push[i][j] = 0

    # DP to count paths
    dp = [[0] * m for _ in range(n)]
    dp[0][0] = 1

    # Fill first row
    for j in range(1, m):
        # Check if moving right from (0, j-1) to (0, j) is valid
        if dp[0][j-1] > 0:
            # Moving right from (0, j-1): pushes right_push[0][j-1] rocks
            # This move is valid if we don't push rocks outside the grid
            if j + right_push[0][j-1] < m:
                dp[0][j] = (dp[0][j] + dp[0][j-1]) % MOD

    # Fill first column
    for i in range(1, n):
        # Check if moving down from (i-1, 0) to (i, 0) is valid
        if dp[i-1][0] > 0:
            # Moving down from (i-1, 0): pushes down_push[i-1][0] rocks
            if i + down_push[i-1][0] < n:
                dp[i][0] = (dp[i][0] + dp[i-1][0]) % MOD

    # Fill rest of the dp table
    for i in range(1, n):
        for j in range(1, m):
            # Coming from left (moving right)
            if dp[i][j-1] > 0:
                # Moving right from (i, j-1): pushes right_push[i][j-1] rocks
                if j + right_push[i][j-1] < m:
                    dp[i][j] = (dp[i][j] + dp[i][j-1]) % MOD

            # Coming from above (moving down)
            if dp[i-1][j] > 0:
                # Moving down from (i-1, j): pushes down_push[i-1][j] rocks
                if i + down_push[i-1][j] < n:
                    dp[i][j] = (dp[i][j] + dp[i-1][j]) % MOD

    print(dp[n-1][m-1])

solve()

