import re

def drop_cap(s):
    # Use regex to find all words (sequences of non-space characters)
    # This preserves spaces exactly as they appear in the original string
    def process_word(match):
        word = match.group(0)
        if len(word) > 2:
            return word.capitalize()
        else:
            return word

    # Replace each word with its processed version
    return re.sub(r'\S+', process_word, s)

# Read input from standard input
s = input()

# Process the string
result = drop_cap(s)

# Print the result to standard output
print(result)

