def can_convert(string1, string2):
    if len(string1) != len(string2):
        return False

    # Dictionaries to store the mapping from string1 to string2 and vice versa
    map1to2 = {}
    map2to1 = {}

    for i in range(len(string1)):
        char1 = string1[i]
        char2 = string2[i]

        # Check if there's already a mapping for char1
        if char1 in map1to2:
            if map1to2[char1] != char2:
                return False
        else:
            map1to2[char1] = char2

        # Check if there's already a mapping for char2
        if char2 in map2to1:
            if map2to1[char2] != char1:
                return False
        else:
            map2to1[char2] = char1

    return True

# Read inputs
string1 = input().strip()
string2 = input().strip()

# Check and print result
