def alphabet_war(fight):
    # Define power values for each side
    left_power = {'w': 4, 'p': 3, 'b': 2, 's': 1}
    right_power = {'m': 4, 'q': 3, 'd': 2, 'z': 1}

    # Handle empty string
    if not fight:
        return "Let's fight again!"

    # Convert string to list for easier manipulation
    fight_list = list(fight)
    length = len(fight_list)

    # Mark positions to be destroyed by bombs
    destroyed = [False] * length

    # Process bombs and mark adjacent positions for destruction
    for i in range(length):
        if fight_list[i] == '*':
            # Destroy the bomb itself
            destroyed[i] = True
            # Destroy left neighbor if exists
            if i > 0:
                destroyed[i-1] = True
            # Destroy right neighbor if exists
            if i < length - 1:
                destroyed[i+1] = True

    # Calculate scores for both sides
    left_score = 0
    right_score = 0

    for i in range(length):
        if not destroyed[i]:  # Only count non-destroyed letters
            char = fight_list[i]
            if char in left_power:
                left_score += left_power[char]
            elif char in right_power:
                right_score += right_power[char]

    # Determine winner
    if left_score > right_score:
        return "Left side wins!"
    elif right_score > left_score:
        return "Right side wins!"
    else:
        return "Let's fight again!"

# Read input and call function
fight = input().strip()
result = alphabet_war(fight)
print(result)

