def move_letter(letter, shift):
    return chr((ord(letter) - ord('a') + shift) % 26 + ord('a'))

def is_vowel(letter):
    return letter in 'aeiou'

def transform_char(c):
    # Handle exceptions first
    if c == 'c' or c == 'o':
        new_char = move_letter(c, -1)
    elif c == 'd':
        new_char = move_letter(c, -3)
    elif c == 'e':
        new_char = move_letter(c, -4)
    # Vowels (excluding y)
    elif is_vowel(c):
        new_char = move_letter(c, -5)
    # Consonants
    else:
        new_char = move_letter(c, 9)

    # Revert if transformed to exception letters
    if new_char in 'code':
        return c
    return new_char

def solve(s):
    return ''.join(transform_char(c) for c in s)

# Read input and produce output
input_string = input().strip()
print(solve(input_string))

