import math

def is_madhav_array(arr):
    n = len(arr)

    # Edge cases
    if n <= 1:
        return False

    # Check if length is a triangular number
    # k(k+1)/2 = n => k^2 + k - 2n = 0
    # Using quadratic formula: k = (-1 + sqrt(1 + 8n)) / 2
    discriminant = 1 + 8 * n
    if discriminant < 0:
        return False

    sqrt_discriminant = math.sqrt(discriminant)
    if not sqrt_discriminant.is_integer():
        return False

    k = int((-1 + sqrt_discriminant) / 2)
    if k * (k + 1) // 2 != n:
        return False

    # Check the Madhav property
    target_sum = arr[0]
    index = 1

    for group_size in range(2, k + 1):
        if index + group_size > n:
            return False

        group_sum = sum(arr[index:index + group_size])
        if group_sum != target_sum:
            return False

        index += group_size

    return True

# Read input and process
if __name__ == "__main__":
    try:
        input_line = input().strip()
        if input_line == "":
            arr = []
        else:
            # Assuming space-separated integers
            arr = list(map(int, input_line.split()))

        result = is_madhav_array(arr)
        print("true" if result else "false")
    except:
        print("false")

