def sieve_of_eratosthenes(limit):
    is_prime = [True] * (limit + 1)
    is_prime[0] = is_prime[1] = False
    for i in range(2, int(limit**0.5) + 1):
        if is_prime[i]:
            for j in range(i*i, limit + 1, i):
                is_prime[j] = False
    primes = [i for i in range(2, limit + 1) if is_prime[i]]
    return is_prime, primes

def main():
    LIMIT = 500000
    is_prime, primes = sieve_of_eratosthenes(LIMIT)

    prime_positions = {}
    for idx, prime in enumerate(primes, 1):
        prime_positions[prime] = idx

    dominant_primes = []
    for prime in primes:
        pos = prime_positions[prime]
        if is_prime[pos]:
            dominant_primes.append(prime)

    a = int(input().strip())
    b = int(input().strip())

    result = sum(p for p in dominant_primes if a <= p <= b)
    print(result)

if __name__ == "__main__":
    main()

