def min_steps_to_target(target):
    if target == 1:
        return 0

    steps = 0
    current = target

    # Work backwards from target to 1
    while current > 1:
        # If current number is even, divide by 2 (reverse of multiply by 2)
        if current % 2 == 0:
            current //= 2
        # If current number is odd, subtract 1 (reverse of add 1)
        else:
            current -= 1
        steps += 1

    return steps

