import math

def factors(n):
    # Validate input
    if not isinstance(n, int) or n < 1:
        return -1

    # Find all factors
    factor_set = set()
    for i in range(1, int(math.sqrt(n)) + 1):
        if n % i == 0:
            factor_set.add(i)
            factor_set.add(n // i)

    # Convert to list, sort in descending order and return
    return sorted(list(factor_set), reverse=True)

# Read input and call function
try:
    num = int(input())
    result = factors(num)
    if result == -1:
        print(-1)
    else:
        print(result)
except ValueError:
    print(-1)

