def is_prime(num):
    if num < 2:
        return False
    if num == 2:
        return True
    if num % 2 == 0:
        return False
    for i in range(3, int(num**0.5) + 1, 2):
        if num % i == 0:
            return False
    return True

def is_mersenne_prime(n):
    # First check if n is prime
    if not is_prime(n):
        return False

    # Check if n is of the form 2^p - 1
    # Add 1 to n to get 2^p
    temp = n + 1

    # Check if temp is a power of 2
    if temp & (temp - 1) != 0:  # This checks if temp is a power of 2
        return False

    # Find p such that 2^p = temp
    p = 0
    while (1 << p) < temp:
        p += 1

    # Now check if p is prime
    return is_prime(p)

# Read input and produce output
n = int(input())
print(is_mersenne_prime(n))

