def mix(s1, s2):
    # Count lowercase letters in both strings
    count1 = {}
    count2 = {}

    for c in s1:
        if 'a' <= c <= 'z':
            count1[c] = count1.get(c, 0) + 1

    for c in s2:
        if 'a' <= c <= 'z':
            count2[c] = count2.get(c, 0) + 1

    # Get all lowercase letters
    all_letters = set(count1.keys()) | set(count2.keys())

    result_parts = []

    for letter in all_letters:
        freq1 = count1.get(letter, 0)
        freq2 = count2.get(letter, 0)
        max_freq = max(freq1, freq2)

        # Only consider letters with max frequency > 1
        if max_freq <= 1:
            continue

        if freq1 > freq2:
            prefix = "1:"
        elif freq2 > freq1:
            prefix = "2:"
        else:  # freq1 == freq2
            prefix = "=:"  # Use "=:" for Python

        result_parts.append(prefix + letter * max_freq)

    # Sort by length (descending), then lexicographically (ascending)
    result_parts.sort(key=lambda x: (-len(x), x))

    return "/".join(result_parts)

# Read input and execute
s1 = input().strip()
s2 = input().strip()
print(mix(s1, s2))

