import sys
import math

def converter(number, decimals=0, base=math.pi):
    if number == 0:
        if decimals == 0:
            return "0"
        else:
            return "0." + "0" * decimals

    # Handle negative numbers
    negative = number < 0
    number = abs(number)

    digits = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ"

    # Convert integer part
    integer_part = int(number)
    if integer_part == 0:
        result = "0"
    else:
        integer_digits = []
        temp = integer_part
        while temp > 0:
            remainder = int(temp % base)
            if remainder >= len(digits):
                remainder = len(digits) - 1
            integer_digits.append(digits[remainder])
            temp = int(temp / base)  # Use regular division for non-integer bases
        result = "".join(reversed(integer_digits))

    # Convert fractional part
    if decimals > 0:
        fractional_part = number - int(number)
        fractional_digits = []
        for _ in range(decimals):
            fractional_part *= base
            digit_index = int(fractional_part)
            if digit_index >= len(digits):
                digit_index = len(digits) - 1
            if digit_index >= int(base):
                digit_index = int(base) - 1
            fractional_digits.append(digits[digit_index])
            fractional_part = fractional_part - digit_index
        result += "." + "".join(fractional_digits)

    if negative:
        result = "-" + result

    return result

# Read input and process
lines = []
try:
    while True:
        line = input().strip()
        if line:
            lines.append(line)
except EOFError:
    pass

if len(lines) == 1:
    print(converter(int(lines[0])))
elif len(lines) == 2:
    print(converter(int(lines[0]), int(lines[1])))
else:
    num = int(lines[0])
    dec = int(lines[1])
    if lines[2] == 'pi':
        base_val = math.pi
    else:
        base_val = float(lines[2])
    print(converter(num, dec, base_val))

