import math
from functools import reduce

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def gcd_list(lst):
    return reduce(gcd, lst)

def frobenius_number(nums):
    # Special case: if 1 is in the list, all numbers are representable
    if 1 in nums:
        return 0

    # Calculate GCD of all numbers
    g = gcd_list(nums)

    # If GCD > 1, there are infinitely many non-representable numbers
    if g > 1:
        return -1

    # For two coprime numbers, use formula
    if len(nums) == 2:
        a, b = nums[0], nums[1]
        return a * b - a - b

    # For more than two numbers, use DP approach
    # Theoretical upper bound for checking is large, but in practice we can limit it
    # Based on computational feasibility, we'll check up to a reasonable limit
    max_check = 100000
    representable = [False] * (max_check + 1)
    representable[0] = True

    for i in range(max_check + 1):
        if representable[i]:
            for num in nums:
                if i + num <= max_check:
                    representable[i + num] = True

    # Find the largest non-representable number
    for i in range(max_check, -1, -1):
        if not representable[i]:
            return i

    # If all numbers up to max_check are representable, return 0
    # This shouldn't happen for valid inputs with GCD=1, but just in case
    return 0

# Read input
nums = eval(input())

# Solve and print result
print(frobenius_number(nums))

