import sys

sys.setrecursionlimit(10000)

def total_inc_dec(x):
    if x == 0:
        return 1

    # Memoization dictionaries
    memo_inc = {}
    memo_dec = {}
    memo_rep = {}

    # Convert 10^x to string to get the length
    s = '9' * x  # All 9s with x digits
    length = x

    # Count increasing numbers with digit DP
    def count_increasing(pos, prev_digit, tight, started):
        if pos == length:
            return 1 if started else 0

        if (pos, prev_digit, tight, started) in memo_inc:
            return memo_inc[(pos, prev_digit, tight, started)]

        limit = int(s[pos]) if tight else 9
        res = 0
        for digit in range(0, limit + 1):
            if not started and digit == 0:
                # Leading zero, don't update prev_digit
                res += count_increasing(pos + 1, prev_digit, tight and (digit == limit), False)
            elif digit >= prev_digit:
                res += count_increasing(pos + 1, digit, tight and (digit == limit), True)

        memo_inc[(pos, prev_digit, tight, started)] = res
        return res

    # Count decreasing numbers with digit DP
    def count_decreasing(pos, prev_digit, tight, started):
        if pos == length:
            return 1 if started else 0

        if (pos, prev_digit, tight, started) in memo_dec:
            return memo_dec[(pos, prev_digit, tight, started)]

        limit = int(s[pos]) if tight else 9
        res = 0
        for digit in range(0, limit + 1):
            if not started and digit == 0:
                # Leading zero, don't update prev_digit
                res += count_decreasing(pos + 1, prev_digit, tight and (digit == limit), False)
            elif digit <= prev_digit:
                res += count_decreasing(pos + 1, digit, tight and (digit == limit), True)

        memo_dec[(pos, prev_digit, tight, started)] = res
        return res

    # Count numbers with all same digits (repeating)
    def count_repeating(pos, prev_digit, tight, started):
        if pos == length:
            return 1 if started else 0

        if (pos, prev_digit, tight, started) in memo_rep:
            return memo_rep[(pos, prev_digit, tight, started)]

        limit = int(s[pos]) if tight else 9
        res = 0
        for digit in range(0, limit + 1):
            if not started and digit == 0:
                # Leading zero
                res += count_repeating(pos + 1, prev_digit, tight and (digit == limit), False)
            elif not started or digit == prev_digit:
                res += count_repeating(pos + 1, digit, tight and (digit == limit), True)

        memo_rep[(pos, prev_digit, tight, started)] = res
        return res

    # Calculate counts
    inc_count = count_increasing(0, 0, True, False)
    dec_count = count_decreasing(0, 9, True, False)
    rep_count = count_repeating(0, 0, True, False)

    # Apply inclusion-exclusion principle
    total = inc_count + dec_count - rep_count

    return total

# Read input and produce output
x = int(input().strip())
print(total_inc_dec(x))

