def exp_sum(n):
    if n == 0:
        return 1

    # Initialize dp array
    dp = [0] * (n + 1)
    dp[0] = 1

    # Generate generalized pentagonal numbers and apply Euler's recurrence
    for i in range(1, n + 1):
        j = 1
        while True:
            # First pentagonal number: j(3j-1)/2
            pent1 = j * (3 * j - 1) // 2
            if pent1 > i:
                break

            # Add or subtract based on parity of j
            if j % 2 == 1:
                dp[i] += dp[i - pent1]
            else:
                dp[i] -= dp[i - pent1]

            # Second pentagonal number: j(3j+1)/2
            pent2 = j * (3 * j + 1) // 2
            if pent2 > i:
                break

            # Add or subtract based on parity of j
            if j % 2 == 1:
                dp[i] += dp[i - pent2]
            else:
                dp[i] -= dp[i - pent2]

            j += 1

    return dp[n]

