def parse_number(s):
    # Define word to number mappings
    units = {
        "zero": 0, "one": 1, "two": 2, "three": 3, "four": 4,
        "five": 5, "six": 6, "seven": 7, "eight": 8, "nine": 9
    }

    teens = {
        "ten": 10, "eleven": 11, "twelve": 12, "thirteen": 13, "fourteen": 14,
        "fifteen": 15, "sixteen": 16, "seventeen": 17, "eighteen": 18, "nineteen": 19
    }

    tens = {
        "twenty": 20, "thirty": 30, "forty": 40, "fifty": 50,
        "sixty": 60, "seventy": 70, "eighty": 80, "ninety": 90
    }

    scales = {
        "hundred": 100,
        "thousand": 1000,
        "million": 1000000
    }

    # Split the input string into words and handle hyphens
    words = []
    for part in s.split():
        if '-' in part:
            # Handle hyphenated words like "forty-six"
            parts = part.split('-')
            words.extend(parts)
        else:
            words.append(part)

    current = 0
    result = 0

    for word in words:
        if word == "and":
            continue
        elif word in units:
            current += units[word]
        elif word in teens:
            current += teens[word]
        elif word in tens:
            current += tens[word]
        elif word in scales:
            if word == "hundred":
                current *= scales[word]
            else:  # thousand or million
                result += current * scales[word]
                current = 0

    return result + current

# Read input and process
input_string = input().strip()
print(parse_number(input_string))

