def proper_fractions(d):
    if d == 1:
        return 0

    result = d

    # Check for factor 2
    if d % 2 == 0:
        result = result // 2
        while d % 2 == 0:
            d //= 2

    # Check for odd factors starting from 3
    p = 3
    while p * p <= d:
        if d % p == 0:
            result = result - result // p
            while d % p == 0:
                d //= p
        p += 2

    # If d is still greater than 1, then it's a prime factor
    if d > 1:
        result = result - result // d

    return result

