def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def main():
    n = int(input())

    if n < 2:
        print(0)
        return

    # Create a list to track which numbers are used
    used = [False] * (n + 1)
    pairs = []

    # First, pair even numbers with each other (they all share factor 2)
    # Collect all even numbers
    evens = []
    for i in range(2, n + 1, 2):
        if not used[i]:
            evens.append(i)

    # Pair consecutive evens
    for i in range(0, len(evens) - 1, 2):
        pairs.append((evens[i], evens[i + 1]))
        used[evens[i]] = True
        used[evens[i + 1]] = True

    # Now pair remaining numbers based on GCD > 1
    # Try all possible pairs among unused numbers
    for i in range(3, n + 1):  # Start from 3, since 1 and 2 are already considered
        if used[i]:
            continue
        for j in range(i + 1, n + 1):
            if used[j]:
                continue
            if gcd(i, j) > 1:
                pairs.append((i, j))
                used[i] = True
                used[j] = True
                break

    print(len(pairs))
    for pair in pairs:
        print(pair[0], pair[1])

if __name__ == "__main__":
    main()

