def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    idx = 0
    n = int(data[idx])
    idx += 1
    k = int(data[idx])
    idx += 1

    grid = []
    for _ in range(4):
        row = list(map(int, data[idx:idx+n]))
        idx += n
        grid.append(row)

    # Positions of cars: pos[i] = (r, c) current position of car i
    # target[i] = (r, c) target position of car i
    pos = [None] * (k + 1)
    target = [None] * (k + 1)

    for r in range(4):
        for c in range(n):
            x = grid[r][c]
            if x != 0:
                if r == 0 or r == 3:  # First or fourth row (target positions)
                    target[x] = (r, c)
                else:  # Second or third row (initial positions)
                    pos[x] = (r, c)

    # Check if already solved
    solved = True
    for i in range(1, k+1):
        if pos[i] != target[i]:
            solved = False
            break
    if solved:
        print(0)
        return

    # BFS with state compression
    def get_state_key(positions):
        # Create a compact representation of car positions
        return tuple(positions[1:])

    # Initial state
    initial_positions = [None] * (k + 1)
    for i in range(1, k + 1):
        initial_positions[i] = pos[i]

    initial_state_key = get_state_key(initial_positions)

    queue = deque()
    visited = {initial_state_key: []}  # state -> path

    queue.append((initial_positions[:], []))  # positions, path

    directions = [(-1,0), (1,0), (0,-1), (0,1)]

    while queue:
        current_positions, path = queue.popleft()

        if len(path) >= 20000:
            continue

        # Check if solved
        is_solved = True
        for i in range(1, k+1):
            if current_positions[i] != target[i]:
                is_solved = False
                break
        if is_solved:
            print(len(path))
            for move in path:
                print(move)
            return

        # Create grid from current positions for easier manipulation
        current_grid = [[0] * n for _ in range(4)]
        for i in range(1, k + 1):
            r, c = current_positions[i]
            current_grid[r][c] = i

        # Find all empty spaces
        empty_spaces = []
        for r in range(4):
            for c in range(n):
                if current_grid[r][c] == 0:
                    empty_spaces.append((r, c))

        # For each empty space, try to move a neighbor car into it
        for er, ec in empty_spaces:
            for dr, dc in directions:
                nr, nc = er + dr, ec + dc
                # Check bounds
                if 0 <= nr < 4 and 0 <= nc < n:
                    car_id = current_grid[nr][nc]
                    if car_id == 0:
                        continue

                    # Check movement rules
                    # Cars can move freely in rows 2 and 3 (indices 1 and 2)
                    # Cars can only move to their target in rows 1 and 4 (indices 0 and 3)
                    if (er == 0 or er == 3):  # Moving to first or fourth row
                        tr, tc = target[car_id]
                        if (tr, tc) != (er, ec):
                            continue  # Not the car's designated spot

                    # Make the move
                    new_positions = current_positions[:]
                    new_positions[car_id] = (er, ec)

                    new_state_key = get_state_key(new_positions)

                    if new_state_key not in visited:
                        if len(path) + 1 <= 20000:
                            move_str = f"{car_id} {er+1} {ec+1}"
                            new_path = path + [move_str]
                            visited[new_state_key] = new_path
                            queue.append((new_positions, new_path))

    print(-1)

if __name__ == "__main__":
    main()

import sys
from collections import defaultdict

class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.rank = [0] * n

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        px, py = self.find(x), self.find(y)
        if px == py:
            return False
        if self.rank[px] < self.rank[py]:
            px, py = py, px
        self.parent[py] = px
        if self.rank[px] == self.rank[py]:
            self.rank[px] += 1
        return True

def solve(n, m, a, b, edges):
    # Separate edges by weight
    edges_a = [(u-1, v-1, a) for u, v, c in edges if c == a]
    edges_b = [(u-1, v-1, b) for u, v, c in edges if c == b]

    # Build MST using Kruskal's algorithm - prioritize lighter edges
    uf = UnionFind(n)
    mst_edges = []

    # First process all edges with weight a
    for u, v, w in edges_a:
        if uf.union(u, v):
            mst_edges.append((u, v, w))

    # Then process all edges with weight b
    for u, v, w in edges_b:
        if uf.union(u, v):
            mst_edges.append((u, v, w))

    # Build adjacency list for MST
    adj = defaultdict(list)
    for u, v, w in mst_edges:
        adj[u].append((v, w))
        adj[v].append((u, w))

    # Compute distances from node 0 (original node 1) using BFS/DFS
    dist = [-1] * n
    dist[0] = 0
    stack = [0]

    while stack:
        u = stack.pop()
        for v, w in adj[u]:
            if dist[v] == -1:
                dist[v] = dist[u] + w
                stack.append(v)

    return dist

def main():
    input_lines = sys.stdin.read().splitlines()
    n, m, a, b = map(int, input_lines[0].split())

    edges = []
    for i in range(1, m + 1):
        u, v, c = map(int, input_lines[i].split())
        edges.append((u, v, c))

    result = solve(n, m, a, b, edges)
    print(' '.join(map(str, result)))

if __name__ == "__main__":
    main()

