import sys
from collections import defaultdict, deque

def main():
    import sys
    sys.setrecursionlimit(10000)

    n = int(sys.stdin.readline())

    if n == 1:
        print("1.0000000000")
        return

    # Build adjacency list
    adj = defaultdict(list)
    edges = []

    for _ in range(n - 1):
        u, v = map(int, sys.stdin.readline().split())
        adj[u].append(v)
        adj[v].append(u)
        edges.append((u, v))

    # Probability memoization
    memo = {}

    def compute_prob(vertex, graph_adj, graph_edges):
        # graph_edges is a set of tuples (u,v) with u < v
        # graph_adj is adjacency dict
        key_edges = tuple(sorted(graph_edges))
        if (vertex, key_edges) in memo:
            return memo[(vertex, key_edges)]

        if len(graph_edges) == 0:
            # Only one vertex left, if it's our target vertex, prob is 1
            nodes = set()
            for u, v in graph_edges:
                nodes.add(u)
                nodes.add(v)
            if len(nodes) == 0:
                # This case shouldn't happen as we check earlier
                return 1.0 if vertex in graph_adj else 0.0
            # Actually, when there are no edges, there's one node
            # So we check if vertex is that node
            # But since we call this only when edges exist...
            pass

        # When there's only one edge, special handling
        if len(graph_edges) == 1:
            u, v = list(graph_edges)[0]
            if vertex == u or vertex == v:
                return 1.0
            else:
                return 0.0

        total_prob = 0.0
        num_edges = len(graph_edges)

        for edge in list(graph_edges):
            u, v = edge
            # Create new graph with this edge contracted
            new_edges = graph_edges.copy()
            new_edges.remove(edge)

            # Remove edges incident to u and v, add new edges to new_node
            neighbors_u = set(graph_adj[u])
            neighbors_v = set(graph_adj[v])

            # Decide which label to keep
            # Case 1: keep u
            new_adj_1 = {node: list(neighbors) for node, neighbors in graph_adj.items()}
            new_node_1 = u

            # Remove u and v
            combined_neighbors_1 = (neighbors_u | neighbors_v) - {u, v}

            new_adj_1[new_node_1] = list(combined_neighbors_1)
            if u in new_adj_1:
                del new_adj_1[u]
            if v in new_adj_1:
                del new_adj_1[v]

            # Update edges: remove any edge involving u or v, add new edges
            new_edge_set_1 = set()
            for (x, y) in new_edges:
                if x == u or x == v:
                    if y != u and y != v:
                        new_edge_set_1.add((min(new_node_1, y), max(new_node_1, y)))
                elif y == u or y == v:
                    if x != u and x != v:
                        new_edge_set_1.add((min(new_node_1, x), max(new_node_1, x)))
                else:
                    new_edge_set_1.add((x, y))

            p1 = compute_prob(vertex, new_adj_1, new_edge_set_1)

            # Case 2: keep v
            new_adj_2 = {node: list(neighbors) for node, neighbors in graph_adj.items()}
            new_node_2 = v

            new_adj_2[new_node_2] = list(combined_neighbors_1)
            if u in new_adj_2:
                del new_adj_2[u]
            if v in new_adj_2:
                del new_adj_2[v]

            new_edge_set_2 = set()
            for (x, y) in new_edges:
                if x == u or x == v:
                    if y != u and y != v:
                        new_edge_set_2.add((min(new_node_2, y), max(new_node_2, y)))
                elif y == u or y == v:
                    if x != u and x != v:
                        new_edge_set_2.add((min(new_node_2, x), max(new_node_2, x)))
                else:
                    new_edge_set_2.add((x, y))

            p2 = compute_prob(vertex, new_adj_2, new_edge_set_2)

            total_prob += (1.0 / num_edges) * 0.5 * (p1 + p2)

        memo[(vertex, key_edges)] = total_prob
        return total_prob

    # Initial graph edges as frozenset of sorted tuples
    initial_edges = set()
    for u, v in edges:
        initial_edges.add((min(u, v), max(u, v)))

    result = []
    for vertex in range(1, n+1):
        # Need to make copies for each computation
        adj_copy = {u: list(v_list) for u, v_list in adj.items()}
        prob = compute_prob(vertex, adj_copy, initial_edges)
        result.append(prob)

    for r in result:
        print(f"{r:.10f}")

if __name__ == "__main__":
    main()

