def main():
    MOD = 10**9 + 7

    n, m = map(int, input().split())
    grid = []
    for _ in range(n):
        grid.append(input().strip())

    # Precompute which cells contain rocks
    rocks = set()
    for i in range(n):
        for j in range(m):
            if grid[i][j] == 'R':
                rocks.add((i, j))

