def get_score(roll):
    freq = count_frequencies(roll)

    if has_straight(roll):
        return 1000

    total_points = 0

    # Check for three pairs
    if has_three_pairs(freq):
        total_points += 750

    # Check for other three of a kinds
    for die, count in freq.items():
        if count >= 3:
            base_points = die * 100
            if count > 3:
                bonus_points = base_points * (count - 3)
            total_points += base_points + bonus_points

    # Check for every 1 and every 5
    if freq.get(1, 0) >= 4:
        total_points += 100
    if freq.get(5, 0) >= 4:
        total_points += 50

    return total_points if total_points > 0 else "Zonk"

