def drop_cap(sentence):
    words = sentence.split()
    modified_words = []

    for word in words:
        if len(word) > 2:
            modified_word = word[0].upper() + word[1:].lower()
            modified_words.append(modified_word)
        else:
            modified_words.append(word)

    return ' '.join(modified_words)

# Input and Output
