result = drop_cap(sentence)
print(result)

def generate_transformations(s):
    transformations = []
    for i, char in enumerate(s):
        if char == 'a':
            transformations.append(s[:i] + 'b' + s[i+1:])
        elif char == 'z':
            transformations.append(s[:i] + 'y' + s[i+1:])
        else:
            transformations.append(s[:i] + chr(ord(char) + 1) + s[i+1:])
            transformations.append(s[:i] + chr(ord(char) - 1) + s[i+1:])
    return transformations

def is_palindrome(s):
    return s == s[::-1]

def solve(s):
    transformations = generate_transformations(s)
    for t in transformations:
        if is_palindrome(t):
            return True
    return False

# Take input from standard input
s = input().strip()

# Call the function with the input parameter and print the output
print(solve(s))

def expand_number(n):
    result = []
    i = 0
    while n > 0:
        digit = n % 10
        if digit != 0:
            result.append(f"{digit} * {10 ** i}")
        n //= 10
        i += 1
    return "+".join(result[::-1])

# Taking input and printing the output
input_number = int(input())
print(expand_number(input_number))

