def alphabet_war(fight):
    # Define mappings for sides and their corresponding powers
    left_side = {'w': 4, 'p': 3, 'b': 2, 's': 1}
    right_side = {'m': 4, 'q': 3, 'd': 2, 'z': 1}

    # Initialize scores
    left_score = 0
    right_score = 0

    # Process the string
    i = 0
    while i < len(fight):
        char = fight[i]
        if char == '*':
            # Handle bomb
            if i > 0:
                left_score -= left_side.get(fight[i-1], 0) if fight[i-1] in left_side else 0
                right_score -= right_side.get(fight[i-1], 0) if fight[i-1] in right_side else 0
            if i + 1 < len(fight):
                left_score -= left_side.get(fight[i+1], 0) if fight[i+1] in left_side else 0
                right_score -= right_side.get(fight[i+1], 0) if fight[i+1] in right_side else 0
            i += 2
        elif char in left_side:
            left_score += left_side[char]
            i += 1
        elif char in right_side:
            right_score += right_side[char]
            i += 1
        else:
            i += 1

    # Determine the winner
    if left_score > right_score:
        return "Left side wins!"
    elif right_score > left_score:
        return "Right side wins!"
    else:
        return "Let's fight again!"

# Get input from standard input
fight = input().strip()

# Call the function and print the output
print(alphabet_war(fight))

def battle(player1, player2):
    result = {'player1': [], 'player2': []}

    min_length = min(len(player1), len(player2))

    for i in range(min_length):
        p1_power, p1_toughness = player1[i]
        p2_power, p2_toughness = player2[i]

        p1_damage = min(p1_power, p2_toughness)
        p2_damage = min(p2_power, p1_toughness)

        p1_toughness -= p2_damage
        p2_toughness -= p1_damage

        if p1_toughness > 0:
            result['player1'].append([p1_power, p1_toughness])
        if p2_toughness > 0:
            result['player2'].append([p2_power, p2_toughness])

    if len(player1) > min_length:
        result['player1'] += player1[min_length:]
    if len(player2) > min_length:
        result['player2'] += player2[min_length:]

    return result

