import re

def find_longest_sequence(s):
    max_length = 0
    longest_seq = ""

    for i in range(len(s)):
        for j in range(i + 2, len(s) + 1):
            seq = s[i:j]
            if seq.count('!') > 0 and seq.count('?') > 0:
                if j - i > max_length:
                    max_length = j - i
                    longest_seq = seq

    return longest_seq

