def transform_string(s):
    vowels = "aeiou"
    transformed = []

    for char in s:
        if char in vowels:
            if char == 'a':
                new_char = 't'
            elif char == 'e':
                new_char = 'j'
            elif char == 'i':
                new_char = 'u'
            elif char == 'o':
                new_char = 'p'
            elif char == 'u':
                new_char = 'z'
        elif char == 'c':
            new_char = 'b'
        elif char == 'd':
            new_char = 'a'
        elif char == 'e':
            new_char = 'a'
        elif char == 'o':
            new_char = 'n'
        else:
            ascii_offset = ord('a') if char.islower() else ord('A')
            new_char = chr((ord(char) - ascii_offset + 9) % 26 + ascii_offset)

        transformed.append(new_char)

    return ''.join(transformed)

