def transform_sentence(sentence):
    def transform_word(word):
        if any(char == 'T' or char == 't' for char in word) and word.count('T') + word.count('t') >= 2:
            return word[::-1]
        elif len(word) > 6:
            return word[::-1]
        elif len(word) == 2 or word.endswith(','):
            return word.upper()
        elif len(word) == 1:
            return '0'
        else:
            return word

    words = sentence.split()
    transformed_words = [transform_word(word) for word in words]
    return ' '.join(transformed_words)

