def sieve_of_eratosthenes(limit):
    is_prime = [True] * (limit + 1)
    p = 2
    while (p * p <= limit):
        if (is_prime[p] == True):
            for i in range(p * p, limit + 1, p):
                is_prime[i] = False
        p += 1
    prime_numbers = []
    for p in range(2, limit + 1):
        if is_prime[p]:
            prime_numbers.append(p)
    return prime_numbers

def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def sum_dominant_primes(a, b):
    max_limit = b + 100  # Safety margin to ensure we cover the range
    primes = sieve_of_eratosthenes(max_limit)

    prime_indices = [False] * len(primes)
    prime_count = 0

    # Mark indices that are prime numbers
    for i in range(len(primes)):
        if is_prime(i + 1):
            prime_indices[i] = True
            prime_count += 1

    # Find dominant primes and calculate their sum
    dominant_primes_sum = 0
    for i in range(len(primes)):
        if a <= primes[i] <= b and prime_indices[i]:
            dominant_primes_sum += primes[i]

    return dominant_primes_sum

