def is_polydivisible(n):
    n_str = str(n)
    for i in range(1, len(n_str) + 1):
        if int(n_str[:i]) % i != 0:
            return False
    return True

def find_next_bigger_polydivisible(n):
    while True:
        n += 1
        if is_polydivisible(n):
            return n

