def almostIncreasingSequence(sequence):
    # Initialize variables
    n = len(sequence)
    violation_found = False
    removal_count = 0

    # Iterate through the sequence
    for i in range(1, n):
        if sequence[i] <= sequence[i-1]:
            if not violation_found:
                violation_found = True
            else:
                removal_count += 1
                if removal_count > 1:
                    return False

    # Check the last element with the second-to-last element
    if removal_count == 0 and sequence[n-1] <= sequence[n-2]:
        removal_count += 1

    # Return True if at most one removal was needed
    return removal_count <= 1

