def transform_string(s):
    vowels = "aeiou"
    exceptions = {"c": "b", "o": "n", "d": "a", "e": "a"}
    transformed = ""

    for char in s:
        if char.isalpha():
            if char in vowels:
                # Move vowels back 5 places, wrap around if necessary
                index = (vowels.index(char) - 5) % len(vowels)
                transformed += vowels[index]
            else:
                # Move consonants forward 9 places, wrap around if necessary
                index = (ord(char) - ord('a') + 9) % 26
                transformed += chr(ord('a') + index)

                # Check for exceptions and revert if necessary
                if transformed[-1] in exceptions:
                    transformed = transformed[:-1] + exceptions[transformed[-1]]
        else:
            transformed += char

    return transformed

# Take input from standard input
input_string = input().strip()

# Call the function with the input string and print the output
output_string = transform_string(input_string)
print(output_string)

def modify_sentence(sentence):
    words = sentence.split()
    modified_words = []

    for word in words:
        # Check if the word needs to be converted to uppercase
        if len(word) == 2 or word.endswith(','):
            modified_word = word.upper()
        # Check if the word needs to be converted to a "0"
        elif len(word) == 1:
            modified_word = "0"
        # Check if the word needs to be reversed
        elif len(word) > 6 or word.count('T') + word.count('t') >= 2:
            modified_word = word[::-1]
        else:
            modified_word = word

        modified_words.append(modified_word)

    return ' '.join(modified_words)

# Take input from standard input
sentence = input()

# Call the function with the input sentence
result = modify_sentence(sentence)

# Print the output to standard output
print(result)

