    html_colors = {
        "gold": "ForestGreen",
        "khaki": "LimeGreen",
        "lemonchiffon": "PaleGreen",
        "lightgoldenrodyellow": "SpringGreen",
        "lightyellow": "MintCream",
        "palegoldenrod": "LightGreen",
        "yellow": "Lime"
    }

    if color.lower() in html_colors:
        return html_colors[color.lower()].capitalize()

    def is_yellow(hex_code):
        r, g, b = int(hex_code[1:3], 16), int(hex_code[3:5], 16), int(hex_code[5:7], 16)
        return r > g and g > b

    if len(color) == 7 and color.startswith('#'):
        if is_yellow(color):
            hex_values = list(color[1:])
            hex_values.sort(reverse=True)
            return '#' + ''.join(hex_values)

    return color

