import math

def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def get_nth_prime(n):
    count = 0
    num = 2
    while True:
        if is_prime(num):
            count += 1
            if count == n:
                return num
        num += 1

def sum_of_dominant_primes(a, b):
    total_sum = 0
    nth_prime = 1
    while get_nth_prime(nth_prime) < b:
        prime_value = get_nth_prime(nth_prime)
        if prime_value >= a and prime_value < b:
            if is_prime(nth_prime):
                total_sum += prime_value
        nth_prime += 1
    return total_sum

# Read input values
