from collections import Counter

def get_score(roll):
    counts = Counter(roll)

    def count_points(counts):
        points = 0
        if counts[1] >= 3:
            points += 1000
        if counts[2] >= 3:
            points += 200
        if counts[3] >= 3:
            points += 300
        if counts[4] >= 3:
            points += 400
        if counts[5] >= 3:
            points += 500
        if counts[6] >= 3:
            points += 600
        if counts[1] == 4:
            points += 2 * count_points({1: 3}) * 1
        if counts[2] == 4:
            points += 2 * count_points({2: 3}) * 2
        if counts[3] == 4:
            points += 2 * count_points({3: 3}) * 3
