            points += 4 * count_points({4: 3}) * 4
        if counts[5] == 6:
            points += 4 * count_points({5: 3}) * 5
        if counts[6] == 6:
            points += 4 * count_points({6: 3}) * 6
        if counts[1] == 1 and counts[5] == 1:
            points += 100
        if counts[5] == 2:
            points += 50
        return points

    if all(count < 2 for count in counts.values()):
        return "Zonk"

    max_points = 0
    for mask in range(1 << len(counts)):
