def format_number(num):
    if num == 0:
        return ""

    terms = []
    power = 0

    while num > 0:
        digit = num % 10
        if digit != 0:
            terms.append(f"{digit}*{10**power}")
        num //= 10
        power += 1

    return '+'.join(reversed(terms))

# Taking input from standard input
input_num = int(input())
result = format_number(input_num)
print(result)

