def count_change(amount, coins):
    if amount < 0:
        return None
    if len(coins) == 0:
        return 0 if amount == 0 else None

    n = len(coins)
    dp = [[0 for _ in range(amount + 1)] for _ in range(n + 1)]

    for i in range(n + 1):
        dp[i][0] = 1

    for i in range(1, n + 1):
        for j in range(1, amount + 1):
            if coins[i-1] <= j:
                dp[i][j] = dp[i-1][j] + dp[i][j-coins[i-1]]
            else:
                dp[i][j] = dp[i-1][j]

    return dp[n][amount]

