def count_paths(n, m, labyrinth):
    dp = [[0] * m for _ in range(n)]
    dp[0][0] = 1

    for i in range(n):
        for j in range(m):
            if labyrinth[i][j] == 'R':
                continue

            if i > 0:
                dp[i][j] += dp[i - 1][j]
            if j > 0:
                dp[i][j] += dp[i][j - 1]

            dp[i][j] %= MOD

    return dp[n - 1][m - 1]

# Read input
n, m = map(int, input().split())
