def same_structure_as(a, b):
    if type(a) != type(b):
        return False
    if isinstance(a, list):
        if len(a) != len(b):
            return False
        for i in range(len(a)):
            if not same_structure_as(a[i], b[i]):
                return False
        return True
    else:
        return True

