  ([1, 1, 1], [2, 2, 2]), # True
  ([1, [1, 1]], [2, [2, 2]]), # True
  ([1, [1, 1]], [[2, 2], 2]), # False
  ([1, [1, 1]], [[2], 2]), # False
  ([[[]], [[]]], [[[]], [[]]]), # True
  ([[[]], [[]]], [[1, 1]]), # False
  ([], []), # True
  ([1], [2]), # True
  ([1], [1, 2]) # False
]

