    primes = sieve_of_eratosthenes(max_limit)
    prime_positions = {primes[i]: i + 1 for i in range(len(primes))}
    dominant_primes = [p for p in primes if a <= p <= b and prime_positions[p] % 2 != 0]
    return sum(dominant_primes)

