def min_operations(num):
    dp = [float('inf')] * (num + 1)
    dp[1] = 0
    for i in range(2, num + 1):
        dp[i] = dp[i - 1] + 1
        if i % 2 == 0:
            dp[i] = min(dp[i], dp[i // 2] + 1)
    return dp[num]

