def solve(s):
    vowels = "aeiou"
    num_vowels = sum(1 for char in s if char in vowels)
    num_consonants = len(s) - num_vowels

    if abs(num_vowels - num_consonants) > 1:
        return "failed"

    v_list = [char for char in s if char in vowels]
    c_list = [char for char in s if char not in vowels]

    if num_vowels == num_consonants:
        if v_list[0] < c_list[0]:
            start_char = v_list.pop(0)
        else:
            start_char = c_list.pop(0)
    elif num_vowels > num_consonants:
        start_char = v_list.pop(0)
    else:
        start_char = c_list.pop(0)

    result = [start_char]
    turn = 0
    while v_list or c_list:
        if turn == 0 and v_list:
            result.append(v_list.pop(0))
        elif c_list:
            result.append(c_list.pop(0))
        turn = 1 - turn

    alternatives = [''.join(result[i:i+2]) for i in range(0, len(result)-1, 2)]
    alternatives.sort()

    return alternatives[0]

# Example usage
