    "six": 6, "seven": 7, "eight": 8, "nine": 9
}

teens = {
    "ten": 10, "eleven": 11, "twelve": 12, "thirteen": 13, "fourteen": 14,
    "fifteen": 15, "sixteen": 16, "seventeen": 17, "eighteen": 18, "nineteen": 19
}

tens = {
    "twenty": 20, "thirty": 30, "forty": 40, "fifty": 50,
    "sixty": 60, "seventy": 70, "eighty": 80, "ninety": 90
}

def unit_to_number(word):
    return units.get(word, 0)

def ten_to_twenty(word):
    return teens.get(word, 0)

def ten_to_ninety(word):
    return tens.get(word, 0)

def hundreds_to_number(word):
    hundred = unit_to_number(word[:-4]) * 100
    remainder = word[-3:]
    if remainder == "and":
        remainder = ""
    return hundred + number_from_words(remainder)

def number_from_words(words):
    words = words.replace("-", " ").split()

    total = 0
    i = 0
    while i < len(words):
        word = words[i]

        if word in ["hundred", "thousand", "million"]:
            i += 1
            continue

        if word in ["and"]:
            i += 1
            continue

        if word in units:
            total += unit_to_number(word)
        elif word in teens:
            total += ten_to_twenty(word)
        elif word in tens:
            total += ten_to_ninety(word)
        elif word.endswith("hundred"):
            total += hundreds_to_number(word)
        elif word.endswith("thousand"):
            total += number_from_words(" ".join(words[i+1:i+3])) * 1000
            i += 3
        elif word.endswith("million"):
            total += number_from_words(" ".join(words[i+1:i+3])) * 1000000
            i += 3

    return total

def main():
    word = input().strip()
    number = number_from_words(word)
    print(number)

