    monomials = {}
    terms = poly.replace('-', '+-').split('+')

    for term in terms:
        if term:
            coeff, vars = parse_monomial(term)
            if vars in monomials:
                monomials[vars] += coeff
            else:
                monomials[vars] = coeff

    simplified_terms = []

    for vars, coeff in sorted(monomials.items()):
